sage.input:
@interact
def _(x=input_box(default=(1,2), type=tuple), k=input_box(default=2, type=real)):
    try:
        kx = (k*x[0],k*x[1])
        xarr = arrow2d((0,0),x,color='blue')
        kxarr = arrow2d((0,0),kx,color=(0,.75,0),linestyle='--', thickness='3pt')
        show(xarr+kxarr)
    except:
        show('An error occurred.')
